function presetModeFrom(status) {
    switch (status) {
        case 'Just Arrived': return 'none';
        case 'Some Home': return 'none';
        case 'Home': return 'none';
        case 'Away': return 'eco';
        case 'Extended Away': return 'eco';
    }
}

const presetMode = presetModeFrom(msg.payload);
if (!presetMode) return;

return {
    payload: {
        data: { preset_mode: presetMode }
    }
};
